mapper.select:
SELECT
	count(1)
FROM
	customer_role_distribution


		        		AND customer_role_distribution.manage_role_id=#{manageRoleId} 


		        		AND customer_role_distribution.have_role_id=#{haveRoleId}

mapper.delete:
DELETE FROM customer_role_distribution 


					    AND crd_id=#{crdId}


		        		AND manage_role_id=#{manageRoleId} 


		        		AND have_role_id=#{haveRoleId}
