mapper.select:
SELECT
				 		zp_company_info.company_id,

				 		zp_company_info.name,

				 		zp_company_info.address,

				 		zp_company_info.desc,

				 		zp_company_info.more_desc,

				 		zp_company_info.scale_id,

				 		zp_company_info.regtime,

				 		zp_company_info.financing_level_id,

				 		zp_company_info.industry_id,

				 		zp_company_info.company_nature,

				 		zp_company_info.in_person,

				 		zp_company_info.last_update_time,

				 		zp_company_info.is_delete

FROM
	zp_company_info



					        		AND zp_company_info.name like '%${name}%'



					        		AND zp_company_info.name =#{name}



		        		AND zp_company_info.address=#{address} 


		        		AND zp_company_info.desc=#{desc} 


		        		AND zp_company_info.more_desc=#{moreDesc} 


		        		AND zp_company_info.scale_id=#{scaleId} 


		        		AND zp_company_info.regtime=#{regtime} 


		        		AND zp_company_info.financing_level_id=#{financingLevelId} 


		        		AND zp_company_info.industry_id=#{industryId} 


		        		AND zp_company_info.company_nature=#{companyNature} 


		        		AND zp_company_info.in_person=#{inPerson} 


		        		AND zp_company_info.last_update_time=#{lastUpdateTime} 


		        		AND zp_company_info.is_delete=#{isDelete} 


ORDER BY zp_company_info.company_id desc 

		limit #{skipResults} ,#{maxResults}

SELECT
	count(1)
FROM
	zp_company_info



					        		AND zp_company_info.name like '%${name}%'



					        		AND zp_company_info.name =#{name}


		        		AND zp_company_info.address=#{address} 


		        		AND zp_company_info.desc=#{desc} 


		        		AND zp_company_info.more_desc=#{moreDesc} 


		        		AND zp_company_info.scale_id=#{scaleId} 


		        		AND zp_company_info.regtime=#{regtime} 


		        		AND zp_company_info.financing_level_id=#{financingLevelId} 


		        		AND zp_company_info.industry_id=#{industryId} 


		        		AND zp_company_info.company_nature=#{companyNature} 


		        		AND zp_company_info.in_person=#{inPerson} 


		        		AND zp_company_info.last_update_time=#{lastUpdateTime} 


		        		AND zp_company_info.is_delete=#{isDelete}

mapper.delete:
DELETE FROM zp_company_info 


		        		AND company_id=#{companyId} 


		        		AND name=#{name} 


		        		AND address=#{address} 


		        		AND desc=#{desc} 


		        		AND more_desc=#{moreDesc} 


		        		AND scale_id=#{scaleId} 


		        		AND regtime=#{regtime} 


		        		AND financing_level_id=#{financingLevelId} 


		        		AND industry_id=#{industryId} 


		        		AND company_nature=#{companyNature} 


		        		AND in_person=#{inPerson} 


		        		AND last_update_time=#{lastUpdateTime} 


		        		AND is_delete=#{isDelete}
