mapper.select:
SELECT
				 		zp_company_job_info.job_id,

				 		zp_company_job_info.company_id,

				 		zp_company_job_info.typeId,

				 		zp_company_job_info.name,

				 		zp_company_job_info.report_object,

				 		zp_company_job_info.jobPositionLevelId,

				 		zp_company_job_info.zp_person_count,

				 		zp_company_job_info.expected_year_money_start,

				 		zp_company_job_info.expected_year_money_end,

				 		zp_company_job_info.work_term_start,

				 		zp_company_job_info.work_term_end,

				 		zp_company_job_info.down_team_person_count,

				 		zp_company_job_info.zp_require,

				 		zp_company_job_info.sex,

				 		zp_company_job_info.educationId,

				 		zp_company_job_info.englishLevelId,

				 		zp_company_job_info.age_start,

				 		zp_company_job_info.age_end,

				 		zp_company_job_info.marital_id,

				 		zp_company_job_info.topSpecialty,

				 		zp_company_job_info.job_tempt_text,

				 		zp_company_job_info.job_tempt_item,

				 		zp_company_job_info.desc,

				 		zp_company_job_info.more_desc,

				 		zp_company_job_info.in_person,

				 		zp_company_job_info.in_datetime,

				 		zp_company_job_info.last_update_time,

				 		zp_company_job_info.is_delete,

				 		zp_company_job_demand_keys.key_id as k_key_id ,

				 		zp_company_job_demand_keys.job_id as k_job_id,

				 		zp_company_job_demand_keys.keyword as k_keyword,

				 		zp_company_job_demand_keys.proportion as  k_proportion,

				 		zp_company_job_info.zp_urgency_status_id,

				 		zp_company_job_info.area_id

FROM
	zp_company_job_info LEFT JOIN zp_company_job_demand_keys ON zp_company_job_info.job_id= zp_company_job_demand_keys.job_id



				        		AND zp_company_job_info.name like '%${name}' 



				        		 AND zp_company_job_info.expected_year_money_start>=#{expectedYearMoneyStart} 


				        		 AND zp_company_job_info.expected_year_money_end<=#{expectedYearMoneyEnd}  


				        		 AND zp_company_job_info.work_term_start>=#{workTermStart}  


				        		 AND zp_company_job_info.work_term_end<=#{workTermEnd}  



		        		AND zp_company_job_info.company_id=#{companyId} 


		        		AND zp_company_job_info.typeId=#{typeId} 


		        		AND zp_company_job_info.name=#{name} 


		        		AND zp_company_job_info.report_object=#{reportObject} 


		        		AND zp_company_job_info.jobPositionLevelId=#{jobPositionLevelId} 


		        		AND zp_company_job_info.zp_person_count=#{zpPersonCount} 


		        		AND zp_company_job_info.down_team_person_count=#{downTeamPersonCount} 


		        		AND zp_company_job_info.zp_require=#{zpRequire} 


		        		AND zp_company_job_info.sex=#{sex} 


		        		AND zp_company_job_info.educationId=#{educationId} 


		        		AND zp_company_job_info.englishLevelId=#{englishLevelId} 


		        		AND zp_company_job_info.age_start=#{ageStart} 


		        		AND zp_company_job_info.age_end=#{ageEnd} 


		        		AND zp_company_job_info.marital_id=#{maritalId} 


		        		AND zp_company_job_info.topSpecialty=#{topSpecialty} 


		        		AND zp_company_job_info.job_tempt_text=#{jobTemptText} 


		        		AND zp_company_job_info.job_tempt_item=#{jobTemptItem} 


		        		AND zp_company_job_info.desc=#{desc} 


		        		AND zp_company_job_info.more_desc=#{moreDesc} 


		        		AND zp_company_job_info.in_person=#{inPerson} 


		        		AND zp_company_job_info.in_datetime=#{inDatetime} 


		        		AND zp_company_job_info.last_update_time=#{lastUpdateTime} 


		        		AND zp_company_job_info.is_delete=#{isDelete} 



		        		AND zp_company_job_info.zp_urgency_status_id=#{zpUrgencyStatusId} 


		        		AND zp_company_job_info.area_id=#{areaId} 



		         AND zp_company_job_info.job_id IN

							 ${item} 



ORDER BY zp_company_job_info.job_id desc 

		limit #{skipResults} ,#{maxResults}

SELECT
	count(1)
FROM
	zp_company_job_info



				        		AND zp_company_job_info.name like '%${name}' 



				        		 AND zp_company_job_info.expected_year_money_start>=#{expectedYearMoneyStart} 


				        		 AND zp_company_job_info.expected_year_money_end<=#{expectedYearMoneyEnd}  


				        		 AND zp_company_job_info.work_term_start>=#{workTermStart}  


