mapper.select:
SELECT
				 		zp_jl_expand_info.jl_expand_id,

				 		zp_jl_expand_info.jl_id,

				 		zp_jl_expand_info.jl_file_path,

				 		zp_jl_expand_info.jl_content

FROM
	zp_jl_expand_info




		        		AND zp_jl_expand_info.jl_id=#{jlId} 


		        		AND zp_jl_expand_info.jl_file_path=#{jlFilePath} 


		        		AND zp_jl_expand_info.jl_content=#{jlContent} 


ORDER BY zp_jl_expand_info.jl_expand_id desc 

		limit #{skipResults} ,#{maxResults}

SELECT
	count(1)
FROM
	zp_jl_expand_info




		        		AND zp_jl_expand_info.jl_id=#{jlId} 


		        		AND zp_jl_expand_info.jl_file_path=#{jlFilePath} 


		        		AND zp_jl_expand_info.jl_content=#{jlContent}

mapper.delete:
DELETE FROM zp_jl_expand_info 


		        		AND jl_expand_id=#{jlExpandId} 


		        		AND jl_id=#{jlId} 


		        		AND jl_file_path=#{jlFilePath} 


		        		AND jl_content=#{jlContent}
