mapper.select:
SELECT
				 		zp_job_matching_keys.key_id,

				 		zp_job_matching_keys.job_id,

				 		zp_job_matching_keys.jl_id,

				 		zp_job_matching_keys.keyword,

				 		zp_job_matching_keys.is_match

FROM
	zp_job_matching_keys




		        		AND zp_job_matching_keys.job_id=#{jobId} 


		        		AND zp_job_matching_keys.jl_id=#{jlId} 


		        		AND zp_job_matching_keys.keyword=#{keyword} 


		        		AND zp_job_matching_keys.is_match=#{isMatch} 



		         AND zp_job_matching_keys.key_id IN

							 ${item} 



ORDER BY zp_job_matching_keys.key_id desc 

		limit #{skipResults} ,#{maxResults}

SELECT
	count(1)
FROM
	zp_job_matching_keys




		        		AND zp_job_matching_keys.job_id=#{jobId} 


		        		AND zp_job_matching_keys.jl_id=#{jlId} 


		        		AND zp_job_matching_keys.keyword=#{keyword} 


		        		AND zp_job_matching_keys.is_match=#{isMatch} 


		         AND zp_job_matching_keys.key_id IN

							 ${item}

mapper.delete:
DELETE FROM zp_job_matching_keys 


		        		AND key_id=#{keyId} 


		        		AND job_id=#{jobId} 


		        		AND jl_id=#{jlId} 


		        		AND keyword=#{keyword} 


		        		AND is_match=#{isMatch}
